const buttonNext = document.querySelector(".button__next"),
  buttonPrevious = document.querySelector(".button__previous"),
  catalogSlidePerf = document.querySelector(".catalog__slide1"),
  catalogSlideDrel = document.querySelector(".catalog__slide2"),
  catalogSlideCarouselFirst = document.querySelector(".carousel1"),
  catalogSlideCarouselSecond = document.querySelector(".carousel2");
buttonNext.addEventListener("click", function (e) {
  e.preventDefault(),
    catalogSlideDrel.classList.add("catalog__slide-visible"),
    catalogSlideCarouselSecond.classList.add("carousel__red"),
    catalogSlideCarouselFirst.classList.add("carousel__white");
}),
  buttonPrevious.addEventListener("click", function (e) {
    e.preventDefault(),
      catalogSlideDrel.classList.remove("catalog__slide-visible"),
      catalogSlideCarouselSecond.classList.remove("carousel__red"),
      catalogSlideCarouselFirst.classList.remove("carousel__white");
  });
