const buttonFeedback = document.querySelector(".contacts__button"),
  popupFeedback = document.querySelector(".popup__feedback"),
  popupClose = popupFeedback.querySelector(".popup__close"),
  popupInputName = popupFeedback.querySelector(".popup__name");
buttonFeedback.addEventListener("click", function (e) {
  e.preventDefault(),
    popupFeedback.classList.add("popup__feedback-visible"),
    popupInputName.focus();
}),
  popupClose.addEventListener("click", function (e) {
    e.preventDefault(),
      popupFeedback.classList.remove("popup__feedback-visible");
  }),
  window.addEventListener("keydown", function (e) {
    const p = e.key;
    ("ESC" !== p && "Escape" !== p) ||
      popupFeedback.classList.remove("popup__feedback-visible");
  });
