const buttonMap = document.querySelector(".button__map"),
  popupMap = document.querySelector(".popup__map"),
  mapClose = popupMap.querySelector(".popup__close");
buttonMap.addEventListener("click", function (p) {
  p.preventDefault(), popupMap.classList.add("popup__map-visible");
}),
  mapClose.addEventListener("click", function (p) {
    p.preventDefault(), popupMap.classList.remove("popup__map-visible");
  }),
  window.addEventListener("keydown", function (p) {
    const e = p.key;
    ("ESC" !== e && "Escape" !== e) ||
      popupMap.classList.remove("popup__map-visible");
  });
